import Sequelize from 'sequelize'
import {combineResolvers} from 'graphql-resolvers'
import {isAuthenticated, isMessageOwner} from './authorization'
import pubsub, {EVENTS} from '../subscription'

const toCursorHash = string => Buffer.from(string).toString('base64')
const fromCursorHash = string => Buffer.from(string, 'base64').toString('ascii')

export default {
  Query: {
    messages: async (parent, {cursor, limit = 100}, {models}) => {
      const cursorOptions = cursor
        ? {
            where: {
              createdAt: {
                [Sequelize.Op.lt]: fromCursorHash(cursor)
              }
            }
          }
        : {}

      const messages = await models.Message.findAll({
        order: [['createdAt', 'DESC']],
        limit: limit + 1,
        ...cursorOptions
      })

      const hasNextPage = messages.length > limit
      const edges = hasNextPage ? messages.slice(0, -1) : messages

      return {
        edges,
        pageInfo: {
          hasNextPage,
          endCursor: toCursorHash(edges[edges.length - 1].createdAt.toString())
        }
      }
    },
    message: async (parent, {id}, {models}) => {
      return await models.Message.findOne({id})
    }
  },
  Mutation: {
    createMessage: combineResolvers(isAuthenticated, async (parent, {text}, {models, me}) => {
      const message = await models.Message.create({
        text,
        userId: me.id
      })

      pubsub.publish(EVENTS.MESSAGE.CREATED, {
        messageCreated: {message}
      })
      return message
    }),
    deleteMessage: combineResolvers(isAuthenticated, isMessageOwner, async (parent, {id}, {models}) => {
      return await models.Message.destroy({where: {id}})
    })
  },
  Message: {
    user: async (message, args, {models}) => {
      return await models.User.findOne({userId: message.userId})
    }
  },
  Subscription: {
    messageCreated: {
      subscribe: () => pubsub.asyncIterator(EVENTS.MESSAGE.CREATED)
    }
  }
}

